transforms.result:
.bw-list-item {
  border-radius: 32px;
  background: #ffffff;
  box-shadow: 0px 6px 14px 0px #0000000f, 0px -4px 14px 0px #0000000a;

  .bw-list-item-title {
    margin-bottom: 16px;
    font-weight: 500;
    font-size: 20px;
    line-height: 28px;
    letter-spacing: 2%;
    color: #4a4a4a;
  }
  .bw-list-item-content {
    font-family: PingFang SC;
    font-weight: 400;
    overflow: hidden;
    text-overflow: ellipsis;
    font-size: 16px;
    line-height: 22.4px;
    letter-spacing: 2%;
    color: #666666;
  }
  // 添加按钮悬停和点击效果
  .bw-button {
    &:hover,
    &:active {
      background-color: #61cd57 !important;

      span {
        color: #fff;
      }

      img {
        filter: brightness(0) invert(1); // 将图标改为白色
      }
    }
  }
}
